"use client";

import { cn } from "@/lib/utils";
import { useState, useEffect } from "react";
import type { DotLoaderProps, DotColor, DotLoaderSize } from "./types";
import { getAnimationDelay } from "./get-animation-delay";
import "./dot-loader.css";

const SIZE_PRESETS: Record<DotLoaderSize, number> = {
  xs: 4, // 12px total
  sm: 5, // 15px total
  md: 6, // 18px total
  lg: 8, // 24px total
};

const COLOR_CLASSES: Record<DotColor, string> = {
  cyan: "bg-cyan-400 dark:bg-cyan-300",
  purple: "bg-purple-400 dark:bg-purple-300",
  green: "bg-green-400 dark:bg-green-300",
  orange: "bg-orange-400 dark:bg-orange-300",
  pink: "bg-pink-400 dark:bg-pink-300",
  blue: "bg-blue-400 dark:bg-blue-300",
  red: "bg-red-400 dark:bg-red-300",
  yellow: "bg-yellow-400 dark:bg-yellow-300",
};

const COLOR_RGB: Record<DotColor, { light: string; dark: string }> = {
  cyan: { light: "34 211 238", dark: "103 232 249" },
  purple: { light: "192 132 252", dark: "216 180 254" },
  green: { light: "74 222 128", dark: "134 239 172" },
  orange: { light: "251 146 60", dark: "253 186 116" },
  pink: { light: "244 114 182", dark: "249 168 212" },
  blue: { light: "96 165 250", dark: "147 197 253" },
  red: { light: "248 113 113", dark: "252 165 165" },
  yellow: { light: "250 204 21", dark: "253 224 71" },
};

/**
 * A customizable 3x3 dot grid loading animation component.
 *
 * Features:
 * - 56 distinct animation patterns across 11 categories
 * - 8 color options with automatic dark mode variants
 * - 4 preset sizes (xs, sm, md, lg) for common use cases
 * - Built-in accessibility support for reduced motion
 * - Zero external UI dependencies
 *
 * @example
 * ```tsx
 * // Basic usage
 * <DotLoader />
 *
 * // With preset size
 * <DotLoader size="sm" />
 *
 * // With custom pattern and color
 * <DotLoader pattern="spiral-clockwise" color="purple" size="lg" />
 * ```
 */
export function DotLoader({
  pattern = "horizontal-wave",
  color = "cyan",
  size = "md",
  className,
}: DotLoaderProps) {
  const dotSize = SIZE_PRESETS[size];

  const dots = Array.from({ length: 3 }, (_, row) =>
    Array.from({ length: 3 }, (_, col) => ({ row, col }))
  );

  const [randomDelays, setRandomDelays] = useState<number[]>([]);

  useEffect(() => {
    if (pattern === "random") {
      setRandomDelays(Array.from({ length: 9 }, () => Math.random() * 450));
    }
  }, [pattern]);

  const getDelay = (row: number, col: number): number => {
    if (pattern === "random") {
      return randomDelays[row * 3 + col] || 0;
    }
    return getAnimationDelay(pattern, row, col);
  };

  return (
    <div
      role="status"
      aria-label={`Loading animation: ${pattern}`}
      className={cn("inline-grid grid-cols-3", className)}
      style={
        {
          "--glow-color-light": COLOR_RGB[color].light,
          "--glow-color-dark": COLOR_RGB[color].dark,
        } as React.CSSProperties
      }
    >
      {dots.flat().map(({ row, col }) => (
        <div
          key={`${row}-${col}`}
          className={cn("dot", COLOR_CLASSES[color], row === 2 && "dot-last")}
          style={{
            width: `${dotSize}px`,
            height: `${dotSize}px`,
            animationName: "dot-wave-glow",
            animationDuration: "600ms",
            animationTimingFunction: "ease-in-out",
            animationIterationCount: "infinite",
            animationDelay: `${getDelay(row, col)}ms`,
            willChange: "opacity, box-shadow",
          }}
        />
      ))}
    </div>
  );
}

import type { AnimationPattern } from "./types";

const SPIRAL_CLOCKWISE_ORDER = [
  [0, 0],
  [0, 1],
  [0, 2],
  [1, 2],
  [2, 2],
  [2, 1],
  [2, 0],
  [1, 0],
  [1, 1],
] as const;

const SPIRAL_COUNTERCLOCKWISE_ORDER = [
  [0, 0],
  [1, 0],
  [2, 0],
  [2, 1],
  [2, 2],
  [1, 2],
  [0, 2],
  [0, 1],
  [1, 1],
] as const;

const ROTATE_CLOCKWISE_ORDER = [
  [0, 1],
  [0, 2],
  [1, 2],
  [2, 2],
  [2, 1],
  [2, 0],
  [1, 0],
  [0, 0],
  [1, 1],
] as const;

const ROTATE_COUNTERCLOCKWISE_ORDER = [
  [0, 1],
  [0, 0],
  [1, 0],
  [2, 0],
  [2, 1],
  [2, 2],
  [1, 2],
  [0, 2],
  [1, 1],
] as const;

const ORBIT_OUTER_ORDER = [
  [0, 0],
  [0, 1],
  [0, 2],
  [1, 2],
  [2, 2],
  [2, 1],
  [2, 0],
  [1, 0],
] as const;

const CROSS_ROTATE_ORDER = [
  [1, 0],
  [0, 1],
  [1, 2],
  [2, 1],
  [1, 1],
] as const;

const PINWHEEL_ORDER = [
  [1, 1],
  [0, 1],
  [1, 0],
  [2, 1],
  [1, 2],
  [0, 0],
  [0, 2],
  [2, 2],
  [2, 0],
] as const;

const X_PATTERN_ORDER = [
  [0, 0],
  [0, 2],
  [2, 0],
  [2, 2],
  [1, 1],
] as const;

const PLUS_PATTERN_ORDER = [
  [1, 1],
  [0, 1],
  [1, 0],
  [2, 1],
  [1, 2],
] as const;

const FOUR_CORNERS_ORDER = [
  [0, 0],
  [0, 2],
  [2, 2],
  [2, 0],
] as const;

const EDGES_ONLY_ORDER = [
  [0, 1],
  [1, 0],
  [1, 2],
  [2, 1],
] as const;

/**
 * Calculates the animation delay for a dot at the given position
 * based on the selected animation pattern.
 *
 * @param pattern - The animation pattern to use
 * @param row - Row index (0-2)
 * @param col - Column index (0-2)
 * @returns Delay in milliseconds. Returns 999999 for hidden dots in partial patterns.
 */
export function getAnimationDelay(
  pattern: AnimationPattern,
  row: number,
  col: number
): number {
  switch (pattern) {
    case "horizontal-wave":
      return row * 150;

    case "horizontal-wave-reverse":
      return (2 - row) * 150;

    case "vertical-wave":
      return col * 150;

    case "vertical-wave-reverse":
      return (2 - col) * 150;

    case "zigzag-horizontal":
      return (row % 2 === 0 ? col : 2 - col) * 150 + row * 100;

    case "zigzag-vertical":
      return (col % 2 === 0 ? row : 2 - row) * 150 + col * 100;

    case "bounce-horizontal":
      return (row < 1.5 ? row : 2 - row) * 150 + col * 75;

    case "bounce-vertical":
      return (col < 1.5 ? col : 2 - col) * 150 + row * 75;

    case "diagonal-forward":
      return (row + col) * 75;

    case "diagonal-backward":
      return (4 - row - col) * 75;

    case "diagonal-reverse":
      return (row + (2 - col)) * 75;

    case "diagonal-reverse-backward":
      return (2 - row + col) * 75;

    case "center-outward":
      return Math.max(Math.abs(row - 1), Math.abs(col - 1)) * 150;

    case "center-inward":
      return (2 - Math.max(Math.abs(row - 1), Math.abs(col - 1))) * 150;

    case "spiral-clockwise": {
      const index = SPIRAL_CLOCKWISE_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index * 75;
    }

    case "spiral-counterclockwise": {
      const index = SPIRAL_COUNTERCLOCKWISE_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index * 75;
    }

    case "corner-top-left":
      return (row + col) * 150;

    case "corner-top-right":
      return (row + (2 - col)) * 150;

    case "corner-bottom-left":
      return (2 - row + col) * 150;

    case "corner-bottom-right":
      return (2 - row + (2 - col)) * 150;

    case "corners-inward":
      return Math.min(Math.abs(row - 1), Math.abs(col - 1)) * 150;

    case "rotate-clockwise": {
      const index = ROTATE_CLOCKWISE_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index * 75;
    }

    case "rotate-counterclockwise": {
      const index = ROTATE_COUNTERCLOCKWISE_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index * 75;
    }

    case "cross-rotate": {
      const index = CROSS_ROTATE_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index !== -1 ? index * 75 : 999999;
    }

    case "pinwheel": {
      const index = PINWHEEL_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index * 75;
    }

    case "checkerboard-a":
      return (row + col) % 2 === 0 ? 0 : 300;

    case "checkerboard-b":
      return (row + col) % 2 === 1 ? 0 : 300;

    case "alternating-rows":
      return (row % 2 === 0 ? 0 : 300) + col * 50;

    case "alternating-columns":
      return (col % 2 === 0 ? 0 : 300) + row * 50;

    case "x-pattern": {
      const index = X_PATTERN_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index !== -1 ? index * 75 : 999999;
    }

    case "plus-pattern": {
      const index = PLUS_PATTERN_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index !== -1 ? index * 150 : 999999;
    }

    case "single-row":
      return row === 1 ? col * 100 : 999999;

    case "single-column":
      return col === 1 ? row * 100 : 999999;

    case "cross-expand":
      return row === 1 || col === 1
        ? (Math.abs(row - 1) + Math.abs(col - 1)) * 150
        : 999999;

    case "random":
      return 0;

    case "single-dot-center":
      return row === 1 && col === 1 ? 0 : 999999;

    case "top-row":
      return row === 0 ? col * 150 : 999999;

    case "bottom-row":
      return row === 2 ? col * 150 : 999999;

    case "left-column":
      return col === 0 ? row * 150 : 999999;

    case "right-column":
      return col === 2 ? row * 150 : 999999;

    case "opposite-corners-a":
      return row === 0 && col === 0 ? 0 : row === 2 && col === 2 ? 300 : 999999;

    case "opposite-corners-b":
      return row === 0 && col === 2 ? 0 : row === 2 && col === 0 ? 300 : 999999;

    case "four-corners-only": {
      const index = FOUR_CORNERS_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index !== -1 ? index * 100 : 999999;
    }

    case "frame-wave": {
      const index = ORBIT_OUTER_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index !== -1 ? index * 75 : 999999;
    }

    case "frame-bounce": {
      const index = ORBIT_OUTER_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      if (index === -1) return 999999;
      const forwardDelay = index * 75;
      const backwardDelay = (ORBIT_OUTER_ORDER.length - 1 - index) * 75;
      return Math.min(forwardDelay, backwardDelay);
    }

    case "edges-only": {
      const index = EDGES_ONLY_ORDER.findIndex(
        ([r, c]) => r === row && c === col
      );
      return index !== -1 ? index * 100 : 999999;
    }

    case "quad-top-left":
      return row <= 1 && col <= 1 ? (row + col) * 100 : 999999;

    case "quad-top-right":
      return row <= 1 && col >= 1 ? (row + (2 - col)) * 100 : 999999;

    case "quad-bottom-left":
      return row >= 1 && col <= 1 ? (2 - row + col) * 100 : 999999;

    case "quad-bottom-right":
      return row >= 1 && col >= 1 ? (2 - row + (2 - col)) * 100 : 999999;

    case "l-shape-tl":
      return row === 0 || col === 0 ? (row + col) * 100 : 999999;

    case "l-shape-tr":
      return row === 0 || col === 2 ? (row + (2 - col)) * 100 : 999999;

    case "l-shape-bl":
      return row === 2 || col === 0 ? (2 - row + col) * 100 : 999999;

    case "l-shape-br":
      return row === 2 || col === 2 ? (2 - row + (2 - col)) * 100 : 999999;

    case "t-shape-horizontal":
      return row === 0 || col === 1 ? (row + Math.abs(col - 1)) * 100 : 999999;

    case "t-shape-vertical":
      return col === 0 || row === 1 ? (col + Math.abs(row - 1)) * 100 : 999999;

    default:
      return row * 150;
  }
}

/* Dot Loader Animations */
.dot {
  opacity: 0;
  box-shadow: 0 0 2px 1px rgb(var(--glow-color-light) / 0.3);
}

.dark .dot {
  box-shadow: 0 0 3px 1px rgb(var(--glow-color-dark) / 0.4);
}

@keyframes dot-wave-glow {
  0%,
  25% {
    opacity: 1;
  }
  25.01%,
  50% {
    opacity: 0.33;
  }
  50.01%,
  100% {
    opacity: 0;
  }
}

.dot-last {
  animation-name: dot-wave-glow-last !important;
}

@keyframes dot-wave-glow-last {
  0%,
  25% {
    opacity: 1;
  }
  25.01%,
  50% {
    opacity: 0.33;
  }
  50.01%,
  100% {
    opacity: 0;
  }
}

/* Accessibility: Reduce motion */
@media (prefers-reduced-motion: reduce) {
  .dot {
    animation: none !important;
    opacity: 0.5;
  }
}
